import React from 'react';
import {Flex, Image} from 'antd';
import styles from './card.module.scss';

const gradeDictionary = {
  51: 'Молодший бакалавр',
  511: 'Фаховий молодший бакалавр',
  52: 'Молодший спеціаліст',
  6: 'Перший (бакалаврський)',
  101: '(Скасований) Спеціаліст',
  7: 'Другий (магістерський)',
  8: 'Третій (освітньо-науковий)',
  9: 'Четвертий (науковий)',
};

const Card = ({entry}) => {
  return (
    <Flex
      className={styles.card}
      align="center"
      justify="space-between"
      gap="middle"
    >
      <Flex gap="middle">
        {entry.image ? (
          <Image
            className={styles.image}
            src={entry.image}
            width={128}
            height={128}
          />
        ) : (
          <Image
            className={styles.image}
            src="error"
            fallback="data:image/png;base64,"
            width={128}
            height={128}
          />
        )}
        <Flex className={styles.text} gap="small" vertical>
          {entry.as_student.length > 0 ? (
            <>
              <h2>{entry.full_name}</h2>
              {entry.as_student.map(
                (student) =>
                  student.speciality_name !==
                    'Дані в обробці. Будуть оновлені пізніше' && (
                    <p key={student.id}>
                      {student.speciality_name}
                      <span> {gradeDictionary[student.grade]}</span>
                    </p>
                  )
              )}
            </>
          ) : (
            entry.as_worker.length > 0 && (
              <>
                <h2>{entry.full_name}</h2>
                {entry.as_worker.map((worker) => (
                  <p>
                    {worker.post} - {worker.teacher__department__name}
                  </p>
                ))}
              </>
            )
          )}
        </Flex>
      </Flex>
    </Flex>
  );
};

export default Card;
